<?php	require_once('data_funs.inc');	require_once('html_helper.php');	html_output_authed_header("管理我的关注", "page-admin-followees");		$num_followees = get_followees_num($_SESSION['valid_user_id']);		echo "<p class='subtitle'>我的关注 (". $num_followees. ")</p>";		@$followees = get_followees($_SESSION['valid_user_id']);		foreach($followees as $fow){		echo "<div class='followee-item clearfix'>"				. "<a href='person.php?userID=". $fow['UserID']. "'>"					. "<img class='user-avatar' title='". $fow['Username']. "' src='". get_user_profile($fow['UserID']). "' />"				. "</a>"				. "<div class='user-info'>"					. "<p class='user-name'><a href='person.php?userID=". $fow['UserID']. "'>". $fow['Username']. "</a></p>"					. "<p class='goal-info'>"						. "<b>". get_goals_num($fow['UserID'], 'now', false). "</b> 进行"						. " | "						. "<b>". get_goals_num($fow['UserID'], 'now', false). "</b> 待启"						. " | "						. "<b>". get_goals_num($fow['UserID'], 'now', false). "</b> 完成"					. "</p>"				. "</div>"				. "<div class='cmd-wap'>"					. "<a class='cmd' href='follower_proc.php?proc=disfollow&followerID=". $_SESSION['valid_user_id']. "&followeeID=". $fow['UserID']. "'>取消关注</a>"				. "</div>"			. "</div>";	}		html_output_authed_footer();?>